import React from "react";
import ReactDOM from "react-dom/client";

function App() {
  return (
    <div>
      <header />
      <menu />
      <footer />
    </div>
  );
}

function header() {
  return <h2>Fast React Pizza CO.</h2>;
}

function menu() {
  <div>
    <h2>Our menu</h2>
    <pizza />
    <pizza />
    <pizza />
  </div>;
}

function footer() {
  <p>{new Date().toLocaleTimeString()}.We are currently open</p>;
}

function pizza() {
  <div>
    <img src=".pizzas/spinaci.jpg" alt="This is spinaci pizza" />
    <h2>Pizza spinaci</h2>
    <p>Tomato, mozeralla,spinaci nad ricotta cheese</p>
  </div>;
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);
