import React from "react";
import ReactDOM from "react-dom/client";

function App() {
  return (
    <div>
      <Header />
      <Menu />
      <Footer />
    </div>
  );
}

function Header() {
  return <h2>Fast React Pizza CO.</h2>;
}

function Menu() {
  return (
    <div>
      <h2>Our menu</h2>
      <Pizza />
      <Pizza />
      <Pizza />
    </div>
  );
}

function Footer() {
  return <p>{new Date().toLocaleTimeString()}.We are currently open</p>;
}

function Pizza() {
  return (
    <div>
      <img src=".pizzas/spinaci.jpg" alt="This is spinaci pizza" />
      <h2>Pizza spinaci</h2>
      <p>Tomato, mozeralla,spinaci nad ricotta cheese</p>
    </div>
  );
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);
